mapper.select:
SELECT NO , NAME , ID , PASSWORD , GENDER , EMAIL , PHONE , BIRTH , JOIN_DATE , LEV , LAST_LOGIN , APPROVAL FROM MEMBER WHERE 1 = 1
AND ID = #{Id}
AND EMAIL = #{Email}
AND PASSWORD = #{Password}

mapper.update:

UPDATE MEMBER
   SET LAST_LOGIN = GETDATE()
 WHERE ID = #{Id}


UPDATE MEMBER
   SET PASSWORD = #{Password}
 WHERE ID = #{Id}
